void Screen1ViewBase::buttonCallbackHandler(const touchgfx::AbstractButton& src)
{
    if (&src == &buttonLSBUSB)
    {
        //Interaction1
        //When buttonLSBUSB clicked call virtual function
        //Call toggleLSBUSB
        toggleLSBUSB();
    }
    if (&src == &buttonToggleBand)
    {
        //Interaction2
        //When buttonToggleBand clicked call virtual function
        //Call selectBand
        selectBand();
    }
    if (&src == &buttonPTT)
    {
        //Interaction3
        //When buttonPTT clicked call virtual function
        //Call toggleRXTX
        toggleRXTX();
    }
    if (&src == &buttonUP)
    {
        //Interaction7
        //When buttonUP clicked call virtual function
        //Call setWFBWup
        setWFBWup();
    }
    if (&src == &buttonDOWN)
    {
        //Interaction8
        //When buttonDOWN clicked call virtual function
        //Call setWFBWdown
        setWFBWdown();
    }
    if (&src == &buttonVFO)
    {
        //Interaction9
        //When buttonVFO clicked call virtual function
        //Call changeStepSize
        changeStepSize();
    }
}
